%on a 10 classes de sonclasses = {'aa','ee','eh','eu','ii','oe','oh','oo','uu','yy'};%liste des moyennes des classeslisteMean={};%liste des matrices de variances/covariance des classeslisteCov={};[listeMean,listeCov] = apprend2(classes);%-------------RECONNAISSANCE-----------------------%%matrice des scoresmatScores=cell(10,10);%on initialise for i=1:10    for j=1:10        matScores{i,j}=0;    endend%pour chaque classefor c=1:10            for numero=81:100        %on recupere les 20 fichiers de test        %a partir du repertoire Signal        v = [ 'Signal/' classes{c} num2str(numero-1,'%.2d') '.wav'];        vect=wavread(v);        %parametrisation du vecteur        vect=parametrisation_fft32(vect);        %transpos√©e        vect=vect';                        %on calcule le score obtenu pour chaque classe        listeScore=[];        for c2=1:10            listeMean{c2};            listeScore=[listeScore,score(listeMean{c2},listeCov{c2},vect)];        end                %on prend le argmin, ie la classe qui minimise le score        [valmin,Iargmin] = min(listeScore);                %la classe est reconnu comme classe k        k=Iargmin;        %on fait la matrice des scores        matScores{c,k} = matScores{c,k}+1;                    end    end